import { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpansesFilter from "../Expenses/FilterExpanses"

import "./Expenses.css";

const Expenses = (props) => {

  const [filteredDate, setFilteredDate] = useState('2020');

  const saveDate = (newDate) => {
    setFilteredDate(newDate);
  }

  const filteredExpenses = props.data.filter( (expense) => expense.date.getFullYear().toString() === filteredDate)

  let emptyText = <p>There are no expenses.</p>

  if(filteredExpenses.length > 0) {
    emptyText =         filteredExpenses.map((expense) => (
      <ExpenseItem 
        key={expense.id}
        title={expense.title}  
        amount={expense.amount}
        date={expense.date}
      />
    ))
  }

  return (
    <Card className="expenses">
      <ExpansesFilter selected={filteredDate} getNewYear={saveDate} />

      {

      }
    </Card>
  );
}

export default Expenses;
